tinymce.PluginManager.add('leftborder', function(editor, url) {
    editor.addButton('leftborder_btn', {
        text: 'Leftborder',
        icon: false,
        onclick: function() {
            var text_selected = editor.selection.getContent();
            var text_modified = "<div style='border-left: 5px solid #33FFBD;'>"+text_selected+"</div>";
            editor.selection.setContent(text_modified);
        }
    });
});
